INSERT INTO `news` (`id`, `title`, `slug`, `image`, `status`, `content`, `school_id`, `created_at`, `updated_at`) VALUES
(1, 'आदरणीय अभिभावक, विद्यार्थी भाइबहिनीहरू, शिक्षक कर्मचारी तथा शुभचिन्तकज्यूहरूमा जरूरी सूचना !', 'va-tha-ya-ra-tha-bdha-va-ya-pa-ra-bdha-ya-tara-fasa-ta-ena-na-pa-l-ashha-ta-ra-l-ya-sama-bna-thha', 'images/news/1590653750Slider-1.jpg', 'Active', '<p style=\"margin-right: 0px; margin-bottom: 10px; margin-left: 0px; color: rgb(99, 99, 99); line-height: 24px; text-align: justify; font-family: \"Open Sans\", sans-serif;\">बिश्वभर संक्रमणको रूपमा फैलिएको नोबेल कोरोना (Covid-19) भाइरसको प्रभावले हामी सबैजना घरमा सुरक्षित तरिकाले आफ्नो सु-स्वस्थ्यको कामना गर्दै बसेका छौं भन्ने कुरामा दुई मत रहन सक्दैन । तथापी नोबेल कोरोना भाइरस (Covid-19) बाट बच्न अपनाउनुपर्ने सम्पूर्ण सावधानीहरू आफु पनि अपनाउ र नानीबाबुहरूलाई पनि पूर्णरूपमा पालना गराउनुहुनेछ, भन्ने कुरामा हामी बिश्वस्त छौं । नेपाल सरकारले Lockdown को मिति थपेसंगै हाम्रो कार्यतालिका समेत प्रभावित भएको व्यहोरा अनुरोध छ । कक्षा ४ र अन्य कक्षाहरू (रिक्त सिट) को प्रवेश परीक्षाका साथै अन्तिम परीक्षाको नतिजा प्रकाशन समेत अर्को सूचना जारी नहुन्जेलसम्म स्थगित भएको जानकारी गराउदछौँ । साथै, कक्षा १०, ११ र १२ का बाबुनानीहरूलाई घरमै परीक्षाको तयारी निरन्तर जारी राख्न सुचित गरिन्छ। यो महामारीबाट मुलुक छिट्टै मुक्ती हुने र पुन नियमित दैनिक कार्यमा हामी फर्कने अपेक्षा राख्दछौ ।</p><p style=\"margin-right: 0px; margin-bottom: 10px; margin-left: 0px; color: rgb(99, 99, 99); line-height: 24px; text-align: justify; font-family: \"Open Sans\", sans-serif;\">नानीबाबुहरूलाई यो बिदा (Lockdown) मा आइटीको उपयुक्त प्रयोग गरि उनीहरूलाई सृजनात्मक कार्यमा व्यस्त राखी दिनुहुन समेत अभिभावकज्यूहरुमा अनुरोध गर्दछौ । अध्ययन सम्बन्धी कुनै समस्या भएमा सम्बन्धित बिषय शिक्षक वा विद्यालयको Facebook मार्फत जानकारी गर्नुहुन सुचित गरिन्छ । </p><p style=\"margin-right: 0px; margin-bottom: 10px; margin-left: 0px; color: rgb(99, 99, 99); line-height: 24px; text-align: justify; font-family: \"Open Sans\", sans-serif;\"><strong style=\"font-weight: bold;\">कृपया तल दिएका सुझावहरू पालना गरि आफु र आफनो परिवारलाई सुरक्षित बनाऊ</strong></p><h1 id=\"mcetoc_1e52nju4v1\" style=\"font-size: 36px; margin-top: 20px; line-height: 28px; color: rgb(51, 51, 51); font-family: \"Open Sans\", sans-serif;\">                  <em>        Be safe, Stay home</em></h1><h1 id=\"mcetoc_1e52nju4v2\" style=\"font-size: 36px; margin-top: 20px; line-height: 28px; color: rgb(51, 51, 51); font-family: \"Open Sans\", sans-serif;\"><strong style=\"font-weight: bold;\">Basic protective measures against the new coronavirus</strong></h1><ul style=\"margin-bottom: 0px; padding-left: 0px; list-style-type: none; color: rgb(51, 51, 51); font-family: \"Open Sans\", sans-serif; font-size: 14px;\"><li style=\"font-size: 15px; color: rgb(32, 50, 69); line-height: 24px; list-style-type: none;\">Maintain social distancing.</li><li style=\"font-size: 15px; color: rgb(32, 50, 69); line-height: 24px; list-style-type: none;\">Wash your hands frequently (regularly and thoroughly clean your hands with soap and water).</li><li style=\"font-size: 15px; color: rgb(32, 50, 69); line-height: 24px; list-style-type: none;\">Avoid touching eyes, nose and mouth.</li><li style=\"font-size: 15px; color: rgb(32, 50, 69); line-height: 24px; list-style-type: none;\">Drink hot water/milk frequently.</li><li style=\"font-size: 15px; color: rgb(32, 50, 69); line-height: 24px; list-style-type: none;\">Do regularly exercise to increase your immunity power.   </li><li style=\"font-size: 15px; color: rgb(32, 50, 69); line-height: 24px; list-style-type: none;\">If you have fever, cough and difficulty breathing, seek medical care early.</li><li style=\"font-size: 15px; color: rgb(32, 50, 69); line-height: 24px; list-style-type: none;\">Stay informed and follow advice given by your healthcare provider.</li></ul><p style=\"margin-right: 0px; margin-bottom: 10px; margin-left: 0px; color: rgb(99, 99, 99); line-height: 24px; text-align: justify; font-family: \"Open Sans\", sans-serif;\"> प्राचार्य<br>गण्डकी बोर्डिङ्ग स्कुल<br>तथा गण्डकी कलेज अफ इन्जिनियरिङ एण्ड साइन्स लामाचौर पोखरा ।</p>', 1, '2020-05-24 12:31:09', '2020-05-28 12:18:23'),
(2, 'बिद्यालय बिदा हुने जरुरी सूचना', 'ma-sa-rab-ta-sara-ta-fa-ka-ta-thha-ta-ma-ra-kha-ra-l-khasama-ma-rinae-yasa-ta-chha-taya-ra', 'images/news/1590653992Classroom-1.jpg', 'Active', '<p style=\"margin-right: 0px; margin-bottom: 1.04em; margin-left: 0px; line-height: 34px; text-align: justify; color: rgb(64, 64, 64); font-family: mukta, sans-serif; font-size: 20px !important;\"><span style=\"color: rgb(99, 99, 99); font-family: &quot;Open Sans&quot;, sans-serif; font-size: 15px;\">मिति २०७६/१२/०६ गते कक्षा ४, ५, ६, ७ र ९ सम्मको अन्तिम परीक्षा विहान १०:०० बजेदेखि र कक्षा १२ को प्रयोगात्मक परीक्षा ससानो समुहमा तोकिएकै समयमा संचालन हुने जानकारी गराइन्छ . साथै कक्षा ८, १०, ११ र AS का आवासीय बिद्यार्थीहरू बिहानै देखि र अन्य बिद्यार्थीहरू परीक्षा ११:३० मा समाप्त भए पश्चात घर जान पाउने हुदा सम्पूर्ण अभिभावकज्यूहरुलाई जानकारीका लागि अनुरोध छ .</span><br></p>', 1, '2020-05-24 12:32:37', '2020-05-28 12:19:52'),
